import React, { useEffect, useState } from "react";
import { MenuItem, Select } from "@mui/material";
import Box from "@mui/material/Box";
import InputLabel from "@mui/material/InputLabel";

import FormControl from "@mui/material/FormControl";
import { wait } from "@testing-library/user-event/dist/utils";
import axios from "axios";

const Country = () => {
  const [countries, setCountries] = useState();
  const [duration, setDuration] = useState("");

  const handleChangeCountry = (event) => {
    setCountries(event.target.value);
  };
  const handleChangeDuration = (event) => {
    setDuration(event.target.value);
  };

  useEffect(() => {
    async function fetchData() {
      const res = await axios.get(
        `https://countriesnow.space/api/v0.1/${countries}/info?returns=currency,flag,unicodeFlag,dialCode`
      );
      console.log(res.data.data);
      setCountries(res.data.data.name);
    }
    fetchData(); //
  });

  return (
    <div>
      <Box
        sx={{
          marginLeft: 15,
          width: 200,
          display: "flex",
          flexDirection: "row",
          flexWrap: "nowrap",
          alignItems: "center",
          float: "left",
        }}
      >
        <FormControl sx={{ flexGrow: 1 }}>
          <InputLabel id="label">Country</InputLabel>
          <Select
            value={countries}
            label="Country"
            onChange={handleChangeCountry}
          >
            <MenuItem value={countries}> {countries}</MenuItem>
            <MenuItem value={10}>Ten</MenuItem>
            <MenuItem value={20}>Twenty</MenuItem>
            <MenuItem value={30}>Thirty</MenuItem>
          </Select>
        </FormControl>
      </Box>
      {
        //now for duration}
      }
      <Box
        sx={{
          marginLeft: 15, //box margin fro left
          width: 200, //box width hai yeh
          display: "flex",
          flexDirection: "row-reverse",
          flexWrap: "nowrap",
          alignItems: "center",
          float: "left",
        }}
      >
        <FormControl sx={{ flexGrow: 1 }}>
          <InputLabel id="label2">Duration</InputLabel>
          <Select
            value={duration}
            label="Duration"
            onChange={handleChangeDuration}
          >
            <MenuItem value={1}>one</MenuItem>
            <MenuItem value={2}>Two</MenuItem>
            <MenuItem value={3}>Thee</MenuItem>
          </Select>
        </FormControl>
      </Box>
      <br></br>
      <p>
        you selected this country {countries} with duration {duration}
      </p>
    </div>
  );
};

export default Country;
