import React, { useEffect, useState } from "react";
import axios from "axios";
import { MenuItem, Select } from "@mui/material";
import { Container, Row, Col, ListGroup, ListGroupItem } from "react-bootstrap";
import Box from "@mui/material/Box";
import InputLabel from "@mui/material/InputLabel";

import FormControl from "@mui/material/FormControl";

const Country = () => {
  const [countries, setCountries] = useState([]);
  useEffect(() => {
    const fetchData = async () => {
      const response = await axios.get(
        "https://countriesnow.space/api/v0.1/countries/info?returns=currency,flag,unicodeFlag,dialCode"
      );

      setCountries(response.data.data);
    };

    fetchData();
  }, [countries]);

  const handleChangeCountry = (event) => {
    setCountries(event.target.value);
  };

  return (
    <Container fluid>
      <Row>
        <Col md="3">
          <Box
            sx={{
              marginLeft: 15,
              width: 200,
              display: "flex",
              flexDirection: "row",
              flexWrap: "nowrap",
              alignItems: "center",
              float: "left",
            }}
          >
            <FormControl sx={{ flexGrow: 1 }}>
              <InputLabel id="label">Country</InputLabel>
              <Select label="Country" onChange={handleChangeCountry}>
                {countries.map((country) => (
                  <MenuItem value={country.name}> {country.name}</MenuItem>
                ))}
              </Select>
            </FormControl>
          </Box>
        </Col>
      </Row>
      <p>you selected this country {countries}</p>
    </Container>
  );
};

export default Country;
